import React from 'react';
import PropTypes from 'prop-types';
import {
  Row,
  Col,
  Card,
  Form,
  Label,
  Alert,
  Input,
  Button,
  CardBody,
  FormGroup,
  CardHeader,
} from 'reactstrap';
import { Link, withRouter } from 'react-router-dom';

class SignUp extends React.Component {
  static propTypes = {
    error: PropTypes.string,
    success: PropTypes.string,
    loading: PropTypes.bool.isRequired,
    onFormSubmit: PropTypes.func.isRequired,
    history: PropTypes.shape({
      push: PropTypes.func.isRequired,
    }).isRequired,
  }

  static defaultProps = {
    error: null,
    success: null,
  }

  state = {
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    password2: '',
  }

  constructor(props) {
    super(props);

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = e => this.setState({ [e.target.name]: e.target.value });

  handleSubmit = (event) => {
    event.preventDefault();
    const { onFormSubmit, history } = this.props;

    onFormSubmit(this.state)
      .then(() => setTimeout(() => history.push('/login'), 1000))
      .catch(() => {});
  }

  render() {
    const { loading, error, success } = this.props;
    const {
      firstName, lastName, email, password, password2,
    } = this.state;

    return (
      <div>
        <Row>
          <Col lg={{ size: 6, offset: 3 }}>
            <Card>
              <CardHeader>Daftar Akaun</CardHeader>
              <CardBody>
                {!!error && <Alert color="danger">{error}</Alert>}
                {!!success && <Alert color="success">{success}</Alert>}

                <Form onSubmit={this.handleSubmit}>
                  <FormGroup>
                    <Label for="firstName">Nama</Label>
                    <Input
                      type="text"
                      name="firstName"
                      id="firstName"
                      placeholder="*Nama penuh anda, seperti tertera di MyKad"
                      disabled={loading}
                      value={firstName}
                      onChange={this.handleChange}
                    />
                  </FormGroup>
                  <FormGroup style={{ marginTop: 40 }}>
                    <Label for="email">E-mel</Label>
                    <Input
                      type="email"
                      name="email"
                      id="email"
                      placeholder="*Contoh_ abdullah@ssipr.sgor.gov.my"
                      disabled={loading}
                      value={email}
                      onChange={this.handleChange}
                    />
                  <p></p>
                  <FormGroup>
                    <Label for="lastName">No Kad Pengenalan</Label>
                    <Input
                      type="text"
                      name="lastName"
                      id="lastName"
                      placeholder="*Contoh_ 760912109022"
                      disabled={loading}
                      value={lastName}
                      onChange={this.handleChange}
                    />
                  </FormGroup>

                  </FormGroup>
                  <FormGroup>
                    <Label for="password">Kata Laluan</Label>
                    <Input
                      type="password"
                      name="password"
                      id="password"
                      placeholder="••••••••"
                      disabled={loading}
                      value={password}
                      onChange={this.handleChange}
                    />
                  </FormGroup>
                  <FormGroup>
                    <Label for="password2">Pengesahan Kata Laluan</Label>
                    <Input
                      type="password"
                      name="password2"
                      id="password2"
                      placeholder="••••••••"
                      disabled={loading}
                      value={password2}
                      onChange={this.handleChange}
                    />
                  </FormGroup>
                  <Button color="primary" disabled={loading}>
                    {loading ? 'Loading' : 'Daftar'}
                  </Button>
                </Form>

                <hr />

                <Row>
                  <Col sm="12">
                    Sudah mendaftar?
                    {' '}
                    <Link to="/login">Log Masuk</Link>
                  </Col>
                </Row>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

export default withRouter(SignUp);
