export type Action = {
  text: string,
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error',
  className?: string,
  onClick?: () => void,
};

interface BaseProps {
  visible?: boolean;
  shape?: 'radius';
  actions: Action[];
  cancelText?: string;
  onMaskClick?: () => void;
  onCancel?: () => void;
}

export interface ActionSheetProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import classnames from 'classnames';
import { ActionSheetProps } from './PropsType';
import Popup from '../Popup';

export { ActionSheetProps };

export default class ActionSheet extends PureComponent<ActionSheetProps, {}> {

  static defaultProps = {
    prefixCls: 'za-actionsheet',
    shape: 'radius',
    visible: false,
    actions: [],
    cancelText: '取消',
  };

  renderActions = (action, index) => {
    const { prefixCls } = this.props;
    const actionCls = classnames(`${prefixCls}-btn`, {
      [`theme-${action.theme}`]: !!action.theme,
    });
    return <a key={+index} className={actionCls} onClick={action.onClick}>{action.text}</a>;
  }

  renderCancel = () => {
    const { prefixCls, onCancel, cancelText } = this.props;
    return (typeof onCancel === 'function') && (
      <div className={`${prefixCls}-cancel`}>
        <a className={`${prefixCls}-btn`} onClick={onCancel}>{cancelText}</a>
      </div>
    );
  }

  render() {
    const { prefixCls, className, shape, visible, onMaskClick, actions } = this.props;
    const cls = classnames(`${prefixCls}`, className, {
      [`shape-${shape}`]: !!shape,
    });

    return (
      <Popup visible={visible} onMaskClick={onMaskClick}>
        <div className={cls}>
          <div className={`${prefixCls}-actions`}>
            {actions.map(this.renderActions)}
          </div>
          {this.renderCancel()}
        </div>
      </Popup>
    );
  }
}

interface BaseProps {
  shape?: 'radius';
  visible?: boolean;
  animationType?:
    'fade' | 'door' | 'flip' | 'rotate' | 'zoom' |
    'moveUp' | 'moveDown' | 'moveLeft' | 'moveRight' |
    'slideUp' | 'slideDown' | 'slideLeft' | 'slideRight';
  animationDuration?: number;
  width?: string | number;
  onMaskClick?: () => void;
  title?: any;
  message?: any;
  cancelText?: any;
  onCancel?: () => void;
}

export interface AlertProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import { AlertProps } from './PropsType';
import Modal from '../Modal';
import Button from '../Button';

export { AlertProps };

export default class Alert extends PureComponent<AlertProps, {}> {

  static defaultProps = {
    prefixCls: 'za-alert',
    animationType: 'zoom',
    message: '',
    cancelText: '关闭',
  };

  render() {
    const { prefixCls, title, message, cancelText, onCancel, ...others } = this.props;

    return (
      <Modal {...others}>
        <Modal.Header title={title} />
        <Modal.Body>
          <div className={prefixCls}>
            {message}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button block bordered onClick={onCancel}>{cancelText}</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}

interface BaseProps {
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error';
  shape?: 'dot' | 'radius' | 'round' | 'circle';
  sup?: boolean;
  text?: any;
}

export interface BadgeProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import classnames from 'classnames';
import { BadgeProps } from './PropsType';

export { BadgeProps };

export default class Badge extends PureComponent<BadgeProps, {}> {

  static defaultProps = {
    prefixCls: 'za-badge',
    theme: 'error',
    sup: false,
  };

  render() {
    const { prefixCls, className, theme, shape, sup, text, children, ...others } = this.props;

    const cls = classnames(`${prefixCls}`, className, {
      [`theme-${theme}`]: !!theme,
      [`shape-${shape}`]: !!shape,
    });

    const supCls = classnames(`${prefixCls}-sup`, {
      [`${prefixCls}-sup-up`]: sup,
    });

    return (
      <span className={cls}>
        {children}
        <sup className={supCls} {...others}>{text}</sup>
      </span>
    );
  }
}

interface BaseProps {
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error';
  size?: 'xl' | 'lg' | 'sm' | 'xs';
  shape?: 'radius' | 'round' | 'circle';
  block?: boolean;
  bordered?: boolean;
  active?: boolean;
  focus?: boolean;
  disabled?: boolean;
  loading?: boolean;
  icon?: any;
  onClick?: (x?: any) => void;
}

export interface ButtonProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import classnames from 'classnames';
import { ButtonProps } from './PropsType';
import Spinner from '../Spinner';

export { ButtonProps };

export default class Button extends PureComponent<ButtonProps, {}> {

  static defaultProps = {
    prefixCls: 'za-button',
    theme: 'default',
    block: false,
    bordered: false,
    active: false,
    focus: false,
    disabled: false,
    loading: false,
    onClick() {},
  };

  render() {
    const {
      prefixCls,
      className,
      theme,
      size,
      shape,
      icon,
      block,
      active,
      focus,
      bordered,
      disabled,
      loading,
      onClick,
      children,
      ...others,
    } = this.props;

    const classes = classnames(`${prefixCls}`, className, {
      [`theme-${theme}`]: !!theme,
      [`size-${size}`]: !!size,
      [`shape-${shape}`]: !!shape,
      block,
      bordered,
      active,
      focus,
      disabled,
    });

    const iconRender = loading
      ? <Spinner className="rotate360" />
      : icon;

    const childrenRender = children && <span>{children}</span>;

    const contentRender = (!!icon || loading)
      ? <div className={`${prefixCls}-content`}>{iconRender}{childrenRender}</div>
      : childrenRender;

    return (
      <a
        role="button"
        aria-disabled={disabled}
        className={classes}
        onClick={e => !disabled && typeof onClick === 'function' && onClick(e)}
        onTouchStart={() => {}}
        {...others}
      >
        {contentRender}
      </a>
    );
  }
}

interface BaseProps {
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error';
  hasArrow?: boolean;
  icon?: any;
  title?: any;
  description?: any;
  help?: any;
  disabled?: boolean;
  onClick?: () => void;
}

export interface CellProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import { CellProps } from './PropsType';
import classnames from 'classnames';

export { CellProps };

export default class Cell extends PureComponent<CellProps, {}> {

  static defaultProps = {
    prefixCls: 'za-cell',
    hasArrow: false,
    disabled: false,
  };

  render() {
    const {
      prefixCls,
      className,
      theme,
      hasArrow,
      icon,
      title,
      description,
      help,
      disabled,
      onClick,
      children,
      ...others,
    } = this.props;

    const cls = classnames(prefixCls, className, {
      [`theme-${theme}`]: !!theme,
      disabled,
      'is-link': !disabled && !!onClick,
      'has-icon': !!icon,
      'has-arrow': hasArrow,
    });

    const iconRender = icon && <div className={`${prefixCls}-icon`}>{icon}</div>;
    const titleRender = title && <div className={`${prefixCls}-title`}>{title}</div>;
    const contentRender = children && <div className={`${prefixCls}-content`}>{children}</div>;
    const arrowRender = hasArrow && <div className={`${prefixCls}-arrow`} />;
    const helpRender = help && (
      <div className={`${prefixCls}-help`}>
        {help}
      </div>
    );

    return (
      <div className={cls} onClick={onClick} onTouchStart={() => {}} {...others}>
        <div className={`${prefixCls}-inner`}>
          <div className={`${prefixCls}-header`}>
            {iconRender}
          </div>
          <div className={`${prefixCls}-body`}>
            {titleRender}
            {contentRender}
          </div>
          <div className={`${prefixCls}-footer`}>
            {description}
          </div>
          {arrowRender}
        </div>
        {helpRender}
      </div>
    );
  }
}

import React, { PureComponent } from 'react';
import classnames from 'classnames';
import { CheckboxProps } from './PropsType';
import Cell from '../Cell';
import Button from '../Button';

function getChecked(props, defaultChecked) {
  if ('checked' in props && props.checked) {
    return props.checked;
  }
  if ('defaultChecked' in props && props.defaultChecked) {
    return props.defaultChecked;
  }
  return defaultChecked;
}

export { CheckboxProps };

export default class Checkbox extends PureComponent<CheckboxProps, any> {

  static Group: any;

  static defaultProps = {
    prefixCls: 'za-checkbox',
    theme: 'primary',
    disabled: false,
  };

  constructor(props) {
    super(props);
    this.state = {
      checked: getChecked(props, false),
    };
  }

  componentWillReceiveProps(nextProps) {
    if ('checked' in nextProps) {
      this.setState({
        checked: !!nextProps.checked,
      });
    }
  }

  onValueChange = () => {
    const { disabled, onChange } = this.props;
    if (disabled) {
      return;
    }

    const checked = !this.state.checked;
    this.setState({ checked });
    if (typeof onChange === 'function') {
      onChange(checked);
    }
  }

  render() {
    const { prefixCls, className, theme, shape, size, type, value, block, disabled, id, children } = this.props;
    const { checked } = this.state;

    const cls = classnames(`${prefixCls}`, className, {
      [`theme-${theme}`]: !!theme,
      [`shape-${shape}`]: !!shape,
      [`size-${size}`]: !!size,
      checked,
      disabled,
    });

    const renderCheckbox = (
      <div className={cls}>
        <div className={`${prefixCls}-wrapper`}>
          <span className={`${prefixCls}-inner`} />
          {children && <span className={`${prefixCls}-text`}>{children}</span>}
          <input
            id={id}
            type="checkbox"
            className={`${prefixCls}-input`}
            disabled={disabled}
            checked={checked}
            onChange={this.onValueChange}
          />
        </div>
      </div>
    );

    if (type === 'cell') {
      return (
        <Cell disabled={disabled} onClick={this.onValueChange}>
          {renderCheckbox}
        </Cell>
      );
    }

    if (type === 'button') {
      return (
        <Button
          className={cls}
          theme={theme}
          shape={shape}
          size="xs"
          block={block}
          bordered={!checked}
          disabled={disabled}
        >
          <input
            type="checkbox"
            className={`${prefixCls}-input`}
            value={value}
            disabled={disabled}
            checked={checked}
            onChange={this.onValueChange}
          />
          {children}
        </Button>
      );
    }

    return renderCheckbox;
  }
}

import React, { PureComponent, cloneElement } from 'react';
import classnames from 'classnames';
import { CheckboxGroupProps } from './PropsType';

function getChildChecked(children) {
  const checkedValue: any[] = [];
  React.Children.map(children, (element: any) => {
    if (element.props && element.props.checked) {
      checkedValue.push(element.props.value);
    }
  });
  return checkedValue;
}

function getValue(props, defaultValue) {
  if ('value' in props) {
    return props.value;
  }
  if ('defaultValue' in props) {
    return props.defaultValue;
  }
  if (getChildChecked(props.children).length > 0) {
    return getChildChecked(props.children);
  }
  return defaultValue;
}

export { CheckboxGroupProps };

export default class CheckboxGroup extends PureComponent<CheckboxGroupProps, any> {

  static defaultProps = {
    prefixCls: 'za-checkbox-group',
    theme: 'primary',
    block: false,
    disabled: false,
    compact: false,
  };

  constructor(props) {
    super(props);
    this.state = {
      value: getValue(props, []),
    };
  }

  componentWillReceiveProps(nextProps) {
    if ('value' in nextProps || getChildChecked(nextProps.children).length > 0) {
      this.setState({
        value: nextProps.value || getChildChecked(nextProps.children),
      });
    }
  }

  onChildChange = (value) => {
    const values = this.state.value;
    const index = values.indexOf(value);

    if (index < 0) {
      values.push(value);
    } else {
      values.splice(index, 1);
    }

    this.setState({
      value: values,
    });

    const { onChange } = this.props;
    if (typeof onChange === 'function') {
      onChange(values);
    }
  }

  render() {
    const { prefixCls, className, theme, shape, type, block, disabled, compact, children } = this.props;

    const items = React.Children.map(children, (element: any, index) => {
      return cloneElement(element, {
        key: index,
        type,
        theme,
        shape,
        block: block || element.props.block,
        disabled: disabled || element.props.disabled,
        onChange: () => this.onChildChange(element.props.value),
        checked: (this.state.value.indexOf(element.props.value) > -1),
      });
    });

    const cls = classnames(`${prefixCls}`, className, {
      [`shape-${shape}`]: !!shape,
      'is-compact': compact,
      block,
      disabled,
    });

    return <div className={cls}>{items}</div>;
  }
}

interface BaseCheckboxProps {
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error';
  shape?: 'radius' | 'round';
  size?: 'lg';
  type?: 'button' | 'cell';
  disabled?: boolean;
  block?: boolean;
  onChange?: (checked: boolean) => void;
  id?: string;
  checked?: boolean;
  defaultChecked?: boolean;
  value?: number | string;
}

export interface CheckboxProps extends BaseCheckboxProps {
  prefixCls?: string;
  className?: string;
}

interface BaseCheckboxGroupProps {
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error';
  shape?: 'radius' | 'round';
  size?: 'lg';
  type?: 'button' | 'cell';
  disabled?: boolean;
  block?: boolean;
  onChange?: (checked: boolean) => void;
  value?: Array<number | string>;
  defaultValue?: Array<number | string>;
  compact?: boolean;
}

export interface CheckboxGroupProps extends BaseCheckboxGroupProps {
  prefixCls?: string;
  className?: string;
}

import Checkbox from './Checkbox';
import CheckboxGroup from './CheckboxGroup';

Checkbox.Group = CheckboxGroup;

export default Checkbox;

interface BaseProps {
  shape?: 'radius';
  visible?: boolean;
  animationType?:
    'fade' | 'door' | 'flip' | 'rotate' | 'zoom' |
    'moveUp' | 'moveDown' | 'moveLeft' | 'moveRight' |
    'slideUp' | 'slideDown' | 'slideLeft' | 'slideRight';
  animationDuration?: number;
  width?: string | number;
  onMaskClick?: () => void;
  title?: any;
  message?: any;
  okText?: string;
  onOk?: () => void;
  cancelText?: string;
  onCancel?: () => void;
}

export interface ConfirmProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import { ConfirmProps } from './PropsType';
import Modal from '../Modal';
import Button from '../Button';

export { ConfirmProps };

export default class Confirm extends PureComponent<ConfirmProps, {}> {

  static defaultProps = {
    prefixCls: 'za-confirm',
    animationType: 'zoom',
    message: '',
    okText: '确定',
    cancelText: '取消',
  };

  render() {
    const { prefixCls, title, message, okText, cancelText, onOk, onCancel, ...others } = this.props;

    return (
      <Modal {...others}>
        <Modal.Header title={title} />
        <Modal.Body>
          <div className={prefixCls}>
            {message}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button block bordered onClick={onCancel}>{cancelText}</Button>
          <Button block bordered theme="primary" onClick={onOk}>{okText}</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}

interface BaseProps {
  onDragStart?: (event?: any, dragState?: Object) => void;
  onDragMove?: (event?: any, dragState?: Object) => void;
  onDragEnd?: (event?: any, dragState?: Object) => void;
  children?: any;
}

export interface DragProps extends BaseProps {}

import { PureComponent, cloneElement } from 'react';
import { DragProps } from './PropsType';

export { DragProps };

export default class Drag extends PureComponent<DragProps, {}> {

  private dragState: any = {};

  onTouchStart = (event) => {
    const dragState = this.dragState;
    const touch = event.touches[0];

    dragState.startX = touch.pageX;
    dragState.startY = touch.pageY;
    dragState.startTime = new Date();

    const { onDragStart } = this.props;
    if (typeof onDragStart === 'function') {
      onDragStart(event, dragState);
    }
  }

  onTouchMove = (event) => {
    const dragState = this.dragState;
    const touch = event.touches[0];

    const currentX = touch.pageX;
    const currentY = touch.pageY;

    const offsetX = currentX - dragState.startX;
    const offsetY = currentY - dragState.startY;

    const state = {
      ...dragState,
      offsetX,
      offsetY,
      currentX,
      currentY,
    };

    const { onDragMove } = this.props;
    if (typeof onDragMove === 'function' && !onDragMove(event, state)) {
      return;
    }

    this.dragState = state;
  }

  onTouchEnd = (event) => {
    const dragState = this.dragState;
    const { onDragEnd } = this.props;
    if (typeof onDragEnd === 'function') {
      onDragEnd(event, dragState);
    }
    this.dragState = {};
  }

  render() {
    const { children } = this.props;
    return cloneElement(children, {
      onTouchStart: this.onTouchStart,
      onTouchMove: this.onTouchMove,
      onTouchEnd: this.onTouchEnd,
    });
  }
}

interface BaseProps {
  type?: string;
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error';
  onClick?: () => void;
}

export interface IconProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import { IconProps } from './PropsType';
import classnames from 'classnames';

export { IconProps };

export default class Icon extends PureComponent<IconProps, {}> {

  static defaultProps = {
    prefixCls: 'za-icon',
  };

  render() {
    const { prefixCls, type, theme, className, ...others } = this.props;
    const cls = classnames(prefixCls, className, {
      [`${prefixCls}-${type}`]: !!type,
      [`theme-${theme}`]: !!theme,
    });

    return (
      <i {...others} className={cls} />
    );
  }
}

import React, { PureComponent } from 'react';
import Toast from '../Toast';
import Spinner from '../Spinner';
import { LoadingProps } from './PropsType';

export { LoadingProps };

class Loading extends PureComponent<LoadingProps, {}> {

  static defaultProps = {
    prefixCls: 'za-loading',
  };

  render() {
    const { prefixCls, ...others } = this.props;

    return (
      <Toast prefixCls={prefixCls} {...others}>
        <Spinner size="lg" className="rotate360" />
      </Toast>
    );
  }
}

export default Loading;

interface BaseProps {
  visible?: boolean;
  type?: 'transparent' | 'light' | 'normal' | 'dark';
  onClose?: () => void;
  style?: React.CSSProperties;
}

export interface MaskProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import classnames from 'classnames';
import { MaskProps } from './PropsType';

export { MaskProps };

export default class Mask extends PureComponent<MaskProps, {}> {

  static defaultProps = {
    prefixCls: 'za-mask',
    visible: false,
    type: 'normal',
  };

  render() {
    const { prefixCls, className, visible, type, onClose, ...others } = this.props;
    const markCls = classnames(`${prefixCls}`, className, type);

    return visible
      ? <div className={markCls} onClick={onClose} {...others} />
      : null;
  }
}

interface BaseProps {
  theme?: 'default' | 'primary' | 'info' | 'success' | 'warning' | 'error';
  size?: 'lg';
  icon?: any;
  hasArrow?: boolean;
  hasClosable?: boolean;
  onClick?: () => void;
}

export interface MessageProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

import React, { PureComponent } from 'react';
import classnames from 'classnames';
import { MessageProps } from './PropsType';
import Icon from '../Icon';

export { MessageProps };

export default class Message extends PureComponent<MessageProps, any> {

  static defaultProps = {
    prefixCls: 'za-message',
    theme: 'primary',
    hasArrow: false,
    hasClosable: false,
  };

  constructor(props) {
    super(props);
    this.state = {
      visible: true,
    };
  }

  onClick = () => {
    const { hasArrow, onClick } = this.props;
    if (hasArrow && typeof onClick === 'function') {
      onClick();
    }
  }

  render() {
    const { prefixCls, className, theme, size, icon, hasArrow, hasClosable, children } = this.props;

    const classes = classnames(`${prefixCls}`, className, {
      [`theme-${theme}`]: !!theme,
      [`size-${size}`]: !!size,
    });

    const iconRender = icon && <div className={`${prefixCls}-icon`}>{icon}</div>;
    const renderCloseIcon = hasClosable && <Icon type="wrong" onClick={() => { this.setState({ visible: false }); }} />;
    const renderArrow = hasArrow && <Icon type="arrow-right" />;
    const noFooter = !hasClosable && !hasArrow;

    return this.state.visible && (
      <div className={classes} onClick={this.onClick}>
        <div className={`${prefixCls}-header`}>{iconRender}</div>
        <div className={`${prefixCls}-body`}>{children}</div>
        {!noFooter && <div className={`${prefixCls}-footer`}>{renderArrow}{renderCloseIcon}</div>}
      </div>
    );
  }
}

import React, { PureComponent, CSSProperties } from 'react';
import classnames from 'classnames';
import Events from '../utils/events';
import { ModalProps } from './PropsType';
import Mask from '../Mask';

export { ModalProps };

export default class Modal extends PureComponent<ModalProps, any> {

  static Header: any;
  static Body: any;
  static Footer: any;
  static defaultProps = {
    prefixCls: 'za-modal',
    visible: false,
    animationType: 'fade',
    animationDuration: 200,
    width: '70%',
  };

  private modal;

  constructor(props) {
    super(props);
    this.state = {
      isShow: props.visible || false,
      isPending: false,
      animationState: 'enter',
    };
  }

  componentWillReceiveProps(nextProps) {
    if (!this.props.visible && nextProps.visible) {
      this.enter();
    } else if (this.props.visible && !nextProps.visible) {
      this.leave();
    }
  }

  componentWillUpdate() {
    Events.on(this.modal, 'webkitAnimationEnd', this.animationEnd);
    Events.on(this.modal, 'animationend', this.animationEnd);
  }

  componentWillUnmount() {
    Events.off(this.modal, 'webkitAnimationEnd', this.animationEnd);
    Events.off(this.modal, 'animationend', this.animationEnd);
  }

  animationEnd = () => {
    if (this.state.animationState === 'leave') {
      this.setState({
        isShow: false,
        isPending: false,
      });
    } else {
      this.setState({
        isShow: true,
        isPending: false,
      });
    }
  }

  enter = () => {
    this.setState({
      isShow: true,
      isPending: true,
      animationState: 'enter',
    });
  }

  leave = () => {
    this.setState({
      isShow: true,
      isPending: true,
      animationState: 'leave',
    });
  }

  render() {
    const { prefixCls, className, shape, animationType, animationDuration, width, onMaskClick, children } = this.props;
    const { isShow, isPending, animationState } = this.state;

    const cls = {
      modal: classnames(`${prefixCls}`, className, {
        [`shape-${shape}`]: !!shape,
        [`fade-${animationState}`]: isPending,
      }),
      dialog: classnames(`${prefixCls}-dialog`, {
        [`${animationType}-${animationState}`]: isPending,
        [`fade-${animationState}`]: isPending,
      }),
      // mask: classnames({
      //   [`fade-${animationState}`]: isPending,
      // }),
    };

    const modalStyle: CSSProperties = {
      WebkitAnimationDuration: `${animationDuration}ms`,
      animationDuration: `${animationDuration}ms`,
    };

    const dialogStyle: CSSProperties = {
      width,
      WebkitAnimationDuration: `${animationDuration}ms`,
      animationDuration: `${animationDuration}ms`,
    };

    // const maskStyle: CSSProperties = {
    //   WebkitAnimationDuration: `${animationDuration}ms`,
    //   MozAnimationDuration: `${animationDuration}ms`,
    //   msAnimationDuration: `${animationDuration}ms`,
    //   OAnimationDuration: `${animationDuration}ms`,
    //   animationDuration: `${animationDuration}ms`,
    // };

    if (!isShow) {
      modalStyle.display = 'none';
    }

    return (
      <div className={cls.modal} style={modalStyle} ref={(ele) => { this.modal = ele; }}>
        <div className={`${prefixCls}-wrapper`}>
          <div className={cls.dialog} style={dialogStyle} onClick={e => e.stopPropagation()}>
            {children}
          </div>
        </div>
        <Mask
          visible={isShow}
          // className={cls.mask}
          // style={maskStyle}
          onClose={onMaskClick}
        />
      </div>
    );
  }
}

