function getChecked(props, defaultChecked) {
  if ('checked' in props && props.checked) {
    return props.checked;
  }
  if ('defaultChecked' in props && props.defaultChecked) {
    return props.defaultChecked;
  }
  return defaultChecked;
}

export { RadioProps };

export default class Radio extends PureComponent<RadioProps, any> {

  static Group: any;
  static defaultProps = {
    prefixCls: 'za-radio',
    theme: 'primary',
    disabled: false,
    block: false,
  };

  constructor(props) {
    super(props);
    this.state = {
      checked: getChecked(props, false),
    };
  }

  componentWillReceiveProps(nextProps) {
    if ('checked' in nextProps) {
      this.setState({
        checked: !!nextProps.checked,
      });
    }
  }

  onValueChange = () => {
    const { disabled, onChange } = this.props;

    if (disabled) {
      return;
    }

    const checked = true;
    this.setState({ checked });
    if (typeof onChange === 'function') {
      onChange(checked);
    }
  }

  render() {
    const { prefixCls, className, type, theme, shape, block, value, disabled, children } = this.props;
    const { checked } = this.state;

    const cls = classnames(`${prefixCls}`, className, {
      [`theme-${theme}`]: !!theme,
      [`shape-${shape}`]: !!shape,
      checked,
      disabled,
    });

    if (type === 'cell') {
      return (
        <Cell
          disabled={disabled}
          description={checked && <Icon type="right" theme={disabled ? undefined : theme} />}
          onClick={() => {}}
        >
          <input
            type="radio"
            className={`${prefixCls}-input`}
            value={value}
            disabled={disabled}
            checked={checked}
            onChange={this.onValueChange}
          />
          {children}
        </Cell>
      );
    }

    if (type === 'button') {
      return (
        <Button
          className={cls}
          theme={theme}
          shape={shape}
          size="xs"
          block={block}
          bordered={!checked}
          disabled={disabled}
        >
          <input
            type="radio"
            className={`${prefixCls}-input`}
            value={value}
            disabled={disabled}
            checked={checked}
            onChange={this.onValueChange}
          />
          {children}
        </Button>
      );
    }

    return (
      <div className={cls}>
        <div className={`${prefixCls}-wrapper`}>
          <span className={`${prefixCls}-inner`} />
          {children && <span className={`${prefixCls}-text`}>{children}</span>}
          <input
            type="radio"
            className={`${prefixCls}-input`}
            disabled={disabled}
            checked={checked}
            onChange={this.onValueChange}
          />
        </div>
      </div>
    );
  }
}

import React, { PureComponent, cloneElement } from 'react';
import classnames from 'classnames';
import { RadioGroupProps } from './PropsType';

function getChildChecked(children) {
  let checkedValue = null;
  React.Children.forEach(children, (element: any) => {
    if (element.props && element.props.checked) {
      checkedValue = element.props.value;
    }
  });
  return checkedValue;
}

function getValue(props, defaultValue) {
  if ('value' in props) {
    return props.value;
  }
  if ('defaultValue' in props) {
    return props.defaultValue;
  }
  if (getChildChecked(props.children)) {
    return getChildChecked(props.children);
  }
  return defaultValue;
}

export { RadioGroupProps };

export default class RadioGroup extends PureComponent<RadioGroupProps, any> {

  static defaultProps = {
    prefixCls: 'za-radio-group',
    theme: 'primary',
    block: false,
    disabled: false,
    compact: false,
  };

  constructor(props) {
    super(props);
    this.state = {
      value: getValue(props, null),
    };
  }

  componentWillReceiveProps(nextProps) {
    if ('value' in nextProps || getChildChecked(nextProps.children)) {
      this.setState({
        value: nextProps.value || getChildChecked(nextProps.children),
      });
    }
  }

  onChildChange = (value) => {
    this.setState({ value });
    const { onChange } = this.props;
    if (typeof onChange === 'function') {
      onChange(value);
    }
  }

  render() {
    const { prefixCls, className, theme, shape, type, block, disabled, compact, children } = this.props;

    const items = React.Children.map(children, (element: any, index) => {
      return cloneElement(element, {
        key: index,
        type,
        theme,
        shape,
        block: block || element.props.block,
        disabled: disabled || element.props.disabled,
        onChange: () => this.onChildChange(element.props.value),
        checked: this.state.value === element.props.value || Number(this.state.value) === Number(element.props.value),
      });
    });

    const cls = classnames(`${prefixCls}`, className, {
      [`shape-${shape}`]: !!shape,
      'is-compact': compact,
      block,
      disabled,
    });

    return <div className={cls}>{items}</div>;
  }
}

