  className?: string;
}

declare global {
  interface Window {
    zarmTooltip?: any;
  }
}

import React, { PureComponent, cloneElement } from 'react';
import ReactDOM, { findDOMNode } from 'react-dom';
import classnames from 'classnames';
import { TooltipProps } from './PropsType';
// import Events from '../utils/events';

export { TooltipProps };

export default class Tooltip extends PureComponent<TooltipProps, any> {

  static defaultProps = {
    prefixCls: 'za-tooltip',
    visible: false,
  };

  private child;
  private tooltip;

  componentDidMount() {
    if (!window.zarmTooltip) {
      window.zarmTooltip = document.createElement('div');
      document.body.appendChild(window.zarmTooltip);
    }
    this.show(this.props);
  }

  componentWillReceiveProps(nextProps) {
    this.show(nextProps);
  }

  show = (props) => {
    const { prefixCls, className, visible, message } = props;
    const cls = classnames(prefixCls, className, {
      [`${prefixCls}-hidden`]: !visible,
    });

    // eslint-disable-next-line
    const rect = findDOMNode(this.child).getBoundingClientRect();
    const scrollTop = document.documentElement.scrollTop + document.body.scrollTop;
    const style = {
      left: rect.left,
      top: rect.top + scrollTop,
      width: rect.width,
    };

    ReactDOM.render(
      <div className={cls} style={style} ref={(ele) => { this.tooltip = ele; }}>
        <div className={`${prefixCls}-inner`}>{message}</div>
      </div>
    , window.zarmTooltip);
  }

  render() {
    const { children } = this.props;

    return cloneElement(children, {
      ref: (ele) => { this.child = ele; },
    });
  }
}

interface BaseProps {
  disabled?: boolean;
  multiple?: boolean;
  quality?: number;
  accept?: string;
  capture?: any;
  onChange?: (file: Object | Array<Object>) => void;
  onBeforeSelect?: () => boolean;
}

export interface UploaderProps extends BaseProps {
  prefixCls?: string;
  className?: string;
}

/**
 * 设置选择前的方法，获取选择文件的相关信息，需要时可以对图片进行压缩、改变图片尺寸。
 *
 * multiple: 默认为 false，设置为 true 之后一次可以选择多张，onChange 事件调用之后返回一个数组，
 *           不设置或者设置为 false，onChange 事件调用之后返回一个对象。
 * disabled: 传递之后不可以点击上传，整个选择组件会设置为半透明状态，透明度为 0.5。
 * quality: 没有默认值，不设置不会进行压缩。
 * accept: 设置选择的文件类型，默认为所有类型，只有文件类型为图片（image/*）的时候会有本地预览图。
 * onChange: () => { file, fileType, fileSize, fileName, thumbnail }。
 * onBeforeSelect: () => boolean，返回 false 的时候阻止后续的选择事件。
 */
import React, { PureComponent } from 'react';
import { UploaderProps } from './PropsType';
import classNames from 'classnames';
import handleFileInfo from './utils/handleFileInfo';

export { UploaderProps };

export default class Uploader extends PureComponent<UploaderProps, any> {

  static defaultProps = {
    prefixCls: 'za-uploader',
    disabled: false,
    multiple: false,
    onBeforeSelect() { return true; },
  };

  private file;

  constructor(props) {
    super(props);
    this.state = {
      fileList: [],
    };
  }

  handleDefaultInput = (e) => {
    // 防止选择同一张图片两次造成 onChange 事件不触发
    e.target.value = null;

    const { onBeforeSelect, disabled } = this.props;
    if (typeof onBeforeSelect !== 'function') {
      return;
    }

    // 阻止 input onChange 默认事件
    if (onBeforeSelect() === false || disabled) {
      e.preventDefault();
    }
  }

  handleClick = () => {
    this.file.click();
  }

  handleChange = (e) => {
    const { onChange, quality, multiple } = this.props;
    const files = [].slice.call(e.target.files);
    const fileList: any[] = [];

    const getFileInfo = (data) => {
      if (multiple) {
        fileList.push(data);

        if (files.length === fileList.length && typeof onChange === 'function') {
          onChange(fileList);
        }
      } else {
        if (typeof onChange === 'function') {
          onChange(data);
        }
      }
    };

    if (files) {
      files.map(file => handleFileInfo({ file, quality }, getFileInfo));
    }
  }

  render() {
    const { prefixCls, className, multiple, accept, capture, disabled, children } = this.props;

    const cls = classNames(prefixCls, className, {
      disabled,
    });

    return (
      <div className={cls}>
        <input
          className={`${prefixCls}-input`}
          type="file"
          ref={(ele) => { this.file = ele; }}
          accept={accept}
          multiple={multiple}
          capture={capture}
          onClick={this.handleDefaultInput}
          onChange={this.handleChange}
        />
        <span className={`${prefixCls}-trigger`} onClick={this.handleClick} />
        {children}
      </div>
    );
  }
}
